/* ==UserStyle==
@name           Logix Account Summary
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Styling for the Logix website for the Account Summary page
@author         huy-vuong
==/UserStyle== */

@-moz-document url-prefix("https://olb.logixbanking.com/User/MainAccounts/List") {

  /* Savings and Checking */
  tr.Data:nth-child(7) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Data:nth-child(8) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Data:nth-child(9) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Total:nth-child(10) > td:nth-child(2) > logix-currency:nth-child(1) > span:nth-child(1) {
    color: #669933;
  }

  tr.Data:nth-child(16) > td:nth-child(6) > span:nth-child(1),
  td.Number:nth-child(3) > span:nth-child(1){
    color: rgb(255, 96, 58);
  }

}

/* ==UserStyle==
@name           Logix
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Styling for the Logix website to add colours to numbers and monospace fonts.
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("logixbanking.com") {

  /* SITE-WIDE */

  #MasterHeaderMenuMain {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  body {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  html {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  .Subtle {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  .Header > td {
    font-weight: 800 !important;
  }

  /* ACCOUNTS */

  /* Account Number */
  .Data > .Word:nth-child(4) > a {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Type */
  .Data > .Word:nth-child(3) > span {
    font-weight: bold;
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Subtotals */
  .Total > .Number {
    font-weight: bold;
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* TRANSACTIONS */

  /* Date */
  .Data > .Word:nth-child(2) > a {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Debit */
  .Data > .Number:nth-child(7) > span > a {
    color: rgb(255, 96, 58);
  }

  /* Credit */
  .Data > .Number:nth-child(8) > span > a {
    color: #669933;
  }

  /* Numeric Fields */
  .Data > .Number {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

}

/* ==UserStyle==
@name           Outlook
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Blocks the ad sidebar in Outlook to avoid it taking up space because I'm a horrible person.
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("outlook.live.com") {
  /* Hide advertisement sidebar. */
  div#app > div.ms-Fabric:first-child > div:nth-child(3) > div:first-child > div:first-child > div:nth-child(4) {
    display: none;
  }
}

/* ==UserStyle==
@name           StackEdit
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Applies a full dark theme to all of the StackEdit UI
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("stackedit.io") {
  .side-title {
    background-color: rgba(255, 255, 255, 0.1);
  }

  .layout__panel--explorer,
  .layout__panel--side-bar {
    background-color: #222;
  }

  .button {
    color: #bbb;
  }

  .button:active,
  .button:focus,
  .button:hover,
  .hidden-file:focus + .button {
    background-color: rgba(255, 255, 255, 0.05);
    color: #ccc;
  }

  .icon * {
    color: #bbb;
  }

  body,
  html {
    color: rgba(255, 255, 255, 0.75);
  }

  .explorer-node--temp,
  .explorer-node--trash {
    color: rgba(255, 255, 255, 0.5);
  }

  .menu-entry__label {
    background-color: #000;
  }

  .explorer-node__item-editor,
  .explorer-node__new-child {
    color: rgba(0, 0, 0, 0.75);
  }

  .modal {
    background-color: hsla(0, 0%, 37%, 0.5);
  }

  .modal__inner-2 {
    background-color: #070707;
  }

  .button--resolve {
    color: #fff !important;
  }
}

