/* ==UserStyle==
@name           Facebook Messenger
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Use Roboto for Facebook Messenger's body fonts instead of Arial.
@author         Me
==/UserStyle== */

@-moz-document domain("www.messenger.com")
{
  body, button, input, label, select, td, textarea, ._z4_
  {
    font-family: '-apple-system', BlinkMacSystemFont, Roboto, 'Segoe UI', Helvetica, Roboto, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol';
  }
}

/* ==UserStyle==
@name           GitHub
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Change body and monospace fonts on GitHub.
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("github.com")
{
  body, .markdown-body
  {
    font-family: '-apple-system',BlinkMacSystemFont,Roboto,'Segoe UI',Helvetica,Roboto,Arial,sans-serif,'Apple Color Emoji','Segoe UI Emoji','Segoe UI Symbol';
  }

  code, pre, tt, .blob-code-inner
  {
    font-family: "JetBrains Mono", "SFMono-Regular", "Cousine", "Consolas", "Liberation Mono", "Menlo", "Courier", monospace;
  }
}

/* ==UserStyle==
@name           Google
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Avoid Arial for Google's fonts.
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("www.google.com")
{
  .g, body, html, input, .std, h1, select
  {
    font-family: Roboto, Arial, sans-serif;
  }

  .gsfi, .lst, .p4wth, .wHYlTd,  .e9EfHf
  {
    font-family: Roboto, Arial, sans-serif;
  }

  .MBeuO, .tNxQIb {
    font-family: "Google Sans", Arial, sans-serif;
    font-weight: 500;
  }
}

/* ==UserStyle==
@name           Logix Account Summary
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Styling for the Logix website for the Account Summary page
@author         huy-vuong
==/UserStyle== */

@-moz-document url-prefix("https://olb.logixbanking.com/User/MainAccounts/List") {
  /* Savings and Checking */
  tr.Data:nth-child(7) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Data:nth-child(8) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Data:nth-child(9) > td:nth-child(6) > logix-currency:nth-child(1) > span:nth-child(1),
  tr.Total:nth-child(10) > td:nth-child(2) > logix-currency:nth-child(1) > span:nth-child(1) {
    color: #669933;
  }

  tr.Data:nth-child(16) > td:nth-child(6) > span:nth-child(1),
  td.Number:nth-child(3) > span:nth-child(1){
    color: rgb(255, 96, 58);
  }
}

/* ==UserStyle==
@name           Logix
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Styling for the Logix website to add colours to numbers and monospace fonts.
@author         huy-vuong
==/UserStyle== */

@-moz-document domain("logixbanking.com") {

  /* SITE-WIDE */

  #MasterHeaderMenuMain {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  body {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  html {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  .Subtle {
    font-family: "SF Pro Text", "Roboto" !important;
  }

  .Header > td {
    font-weight: 800 !important;
  }

  /* ACCOUNTS */

  /* Account Number */
  .Data > .Word:nth-child(4) > a {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Type */
  .Data > .Word:nth-child(3) > span {
    font-weight: bold;
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Subtotals */
  .Total > .Number {
    font-weight: bold;
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* TRANSACTIONS */

  /* Date */
  .Data > .Word:nth-child(2) > a {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

  /* Debit */
  .Data > .Number:nth-child(7) > span > a {
    color: rgb(255, 96, 58);
  }

  /* Credit */
  .Data > .Number:nth-child(8) > span > a {
    color: #669933;
  }

  /* Numeric Fields */
  .Data > .Number {
    font-family: "JetBrains Mono", "SF Mono", "Roboto Mono", "Cousine";
  }

}

/* ==UserStyle==
